import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import {
  Paper,
  Typography,
  TextField,
  Button,
  IconButton,
  Box
} from '@mui/material';
import { Add as AddIcon, Delete as DeleteIcon } from '@mui/icons-material';
import { CVData, Experience, Education } from '../../types/CVTypes';
import { CVService } from '../../services/api/cvService';
import { RequiredLabel } from '../shared/RequiredLabel';

const initialCV: CVData = {
  personalInfo: {
    fullName: '',
    title: '',
    email: '',
    phone: '',
    location: '',
  },
  summary: '',
  experience: [],
  education: [],
  skills: [],
};

const emptyExperience: Experience = {
  title: '',
  company: '',
  period: '',
  description: [''],
};

const emptyEducation: Education = {
  degree: '',
  institution: '',
  period: '',
  details: '',
};

export const CVEdit: React.FC = () => {
  const [cv, setCV] = useState<CVData>(initialCV);
  const history = useHistory();

  const handlePersonalInfoChange = (field: keyof typeof cv.personalInfo) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setCV({
      ...cv,
      personalInfo: {
        ...cv.personalInfo,
        [field]: event.target.value,
      },
    });
  };

  const handleSummaryChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCV({
      ...cv,
      summary: event.target.value,
    });
  };

  const handleExperienceChange = (index: number, field: keyof Experience) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    const newExperience = [...cv.experience];
    newExperience[index] = {
      ...newExperience[index],
      [field]: event.target.value,
    };
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const handleExperienceDescriptionChange = (expIndex: number, descIndex: number) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    const newExperience = [...cv.experience];
    const newDescription = [...newExperience[expIndex].description];
    newDescription[descIndex] = event.target.value;
    newExperience[expIndex] = {
      ...newExperience[expIndex],
      description: newDescription,
    };
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const handleEducationChange = (index: number, field: keyof Education) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    const newEducation = [...cv.education];
    newEducation[index] = {
      ...newEducation[index],
      [field]: event.target.value,
    };
    setCV({
      ...cv,
      education: newEducation,
    });
  };

  const handleSkillsChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCV({
      ...cv,
      skills: event.target.value.split(',').map(skill => skill.trim()),
    });
  };

  const addExperience = () => {
    setCV({
      ...cv,
      experience: [...cv.experience, { ...emptyExperience }],
    });
  };

  const removeExperience = (index: number) => {
    const newExperience = [...cv.experience];
    newExperience.splice(index, 1);
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const addEducation = () => {
    setCV({
      ...cv,
      education: [...cv.education, { ...emptyEducation }],
    });
  };

  const removeEducation = (index: number) => {
    const newEducation = [...cv.education];
    newEducation.splice(index, 1);
    setCV({
      ...cv,
      education: newEducation,
    });
  };

  const addExperienceDescription = (expIndex: number) => {
    const newExperience = [...cv.experience];
    newExperience[expIndex].description.push('');
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const removeExperienceDescription = (expIndex: number, descIndex: number) => {
    const newExperience = [...cv.experience];
    newExperience[expIndex].description.splice(descIndex, 1);
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const handleSubmit = async () => {
    try {
      await CVService.saveCV(cv);
      history.push('/');
    } catch (error) {
      console.error('Error saving CV:', error);
    }
  };

  return (
    <Paper
      elevation={3}
      sx={{
        p: 4,
        maxWidth: 800,
        mx: 'auto',
      }}
    >
      <Box sx={{ mb: 4 }}>
        <Typography
          variant="h5"
          sx={{
            color: 'primary.main',
            mb: 2,
          }}
        >
          المعلومات الشخصية
        </Typography>
        <TextField
          fullWidth
          label={<RequiredLabel label="الاسم الكامل" required />}
          value={cv.personalInfo.fullName}
          onChange={handlePersonalInfoChange('fullName')}
          sx={{ mb: 2 }}
          required
        />
        <TextField
          fullWidth
          label={<RequiredLabel label="المسمى الوظيفي" required />}
          value={cv.personalInfo.title}
          onChange={handlePersonalInfoChange('title')}
          sx={{ mb: 2 }}
          required
        />
        <TextField
          fullWidth
          label={<RequiredLabel label="البريد الإلكتروني" required />}
          value={cv.personalInfo.email}
          onChange={handlePersonalInfoChange('email')}
          sx={{ mb: 2 }}
          required
          type="email"
        />
        <TextField
          fullWidth
          label={<RequiredLabel label="رقم الهاتف" required />}
          value={cv.personalInfo.phone}
          onChange={handlePersonalInfoChange('phone')}
          sx={{ mb: 2 }}
          required
        />
        <TextField
          fullWidth
          label={<RequiredLabel label="الموقع" required />}
          value={cv.personalInfo.location}
          onChange={handlePersonalInfoChange('location')}
          sx={{ mb: 2 }}
          required
        />
      </Box>

      <Box sx={{ mb: 4 }}>
        <Typography
          variant="h5"
          sx={{
            color: 'primary.main',
            mb: 2,
          }}
        >
          الملخص
        </Typography>
        <TextField
          fullWidth
          multiline
          rows={4}
          label={<RequiredLabel label="الملخص المهني" required />}
          value={cv.summary}
          onChange={handleSummaryChange}
          sx={{ mb: 2 }}
          required
        />
      </Box>

      <Box sx={{ mb: 4 }}>
        <Typography
          variant="h5"
          sx={{
            color: 'primary.main',
            mb: 2,
          }}
        >
          الخبرات
        </Typography>
        {cv.experience.map((exp, expIndex) => (
          <Box
            key={expIndex}
            sx={{
              mb: 2,
              p: 2,
              border: 1,
              borderColor: 'divider',
              borderRadius: 1,
              position: 'relative',
            }}
          >
            <IconButton
              sx={{
                position: 'absolute',
                top: 8,
                right: 8,
              }}
              onClick={() => removeExperience(expIndex)}
            >
              <DeleteIcon />
            </IconButton>
            <TextField
              fullWidth
              label={<RequiredLabel label="المسمى الوظيفي" required />}
              value={exp.title}
              onChange={handleExperienceChange(expIndex, 'title')}
              sx={{ mb: 2 }}
              required
            />
            <TextField
              fullWidth
              label={<RequiredLabel label="الشركة" required />}
              value={exp.company}
              onChange={handleExperienceChange(expIndex, 'company')}
              sx={{ mb: 2 }}
              required
            />
            <TextField
              fullWidth
              label={<RequiredLabel label="الفترة" required />}
              value={exp.period}
              onChange={handleExperienceChange(expIndex, 'period')}
              sx={{ mb: 2 }}
              required
              placeholder="مثال: 2019 - 2021"
            />
            {exp.description.map((desc, descIndex) => (
              <Box
                key={descIndex}
                sx={{
                  display: 'flex',
                  gap: 1,
                  mb: 2,
                }}
              >
                <TextField
                  fullWidth
                  label={`الوصف ${descIndex + 1}`}
                  value={desc}
                  onChange={handleExperienceDescriptionChange(expIndex, descIndex)}
                />
                <IconButton
                  onClick={() => removeExperienceDescription(expIndex, descIndex)}
                >
                  <DeleteIcon />
                </IconButton>
              </Box>
            ))}
            <Button
              startIcon={<AddIcon />}
              onClick={() => addExperienceDescription(expIndex)}
            >
              إضافة وصف
            </Button>
          </Box>
        ))}
        <Button
          variant="outlined"
          startIcon={<AddIcon />}
          onClick={addExperience}
          sx={{ mt: 1 }}
        >
          إضافة خبرة
        </Button>
      </Box>

      <Box sx={{ mb: 4 }}>
        <Typography
          variant="h5"
          sx={{
            color: 'primary.main',
            mb: 2,
          }}
        >
          التعليم
        </Typography>
        {cv.education.map((edu, eduIndex) => (
          <Box
            key={eduIndex}
            sx={{
              mb: 2,
              p: 2,
              border: 1,
              borderColor: 'divider',
              borderRadius: 1,
              position: 'relative',
            }}
          >
            <IconButton
              sx={{
                position: 'absolute',
                top: 8,
                right: 8,
              }}
              onClick={() => removeEducation(eduIndex)}
            >
              <DeleteIcon />
            </IconButton>
            <TextField
              fullWidth
              label={<RequiredLabel label="الدرجة العلمية" required />}
              value={edu.degree}
              onChange={handleEducationChange(eduIndex, 'degree')}
              sx={{ mb: 2 }}
              required
            />
            <TextField
              fullWidth
              label={<RequiredLabel label="المؤسسة التعليمية" required />}
              value={edu.institution}
              onChange={handleEducationChange(eduIndex, 'institution')}
              sx={{ mb: 2 }}
              required
            />
            <TextField
              fullWidth
              label={<RequiredLabel label="الفترة" required />}
              value={edu.period}
              onChange={handleEducationChange(eduIndex, 'period')}
              sx={{ mb: 2 }}
              required
              placeholder="مثال: 2015 - 2019"
            />
            <TextField
              fullWidth
              label="التفاصيل"
              value={edu.details}
              onChange={handleEducationChange(eduIndex, 'details')}
              multiline
              rows={2}
            />
          </Box>
        ))}
        <Button
          variant="outlined"
          startIcon={<AddIcon />}
          onClick={addEducation}
          sx={{ mt: 1 }}
        >
          إضافة تعليم
        </Button>
      </Box>

      <Box sx={{ mb: 4 }}>
        <Typography
          variant="h5"
          sx={{
            color: 'primary.main',
            mb: 2,
          }}
        >
          المهارات
        </Typography>
        <TextField
          fullWidth
          multiline
          rows={2}
          label="المهارات (مفصولة بفواصل)"
          value={cv.skills.join(', ')}
          onChange={handleSkillsChange}
          helperText="أدخل المهارات مفصولة بفواصل، مثال: React, TypeScript, Node.js"
        />
      </Box>

      <Button
        variant="contained"
        color="primary"
        size="large"
        fullWidth
        onClick={handleSubmit}
        sx={{ mt: 2 }}
      >
        حفظ السيرة الذاتية
      </Button>
    </Paper>
  );
};
