import { CVData } from '../../types/CVTypes';

// سنستخدم localStorage مؤقتاً لتخزين البيانات
const STORAGE_KEY = 'cv_data';

export const CVService = {
  async getCVById(id: string): Promise<CVData> {
    const data = localStorage.getItem(STORAGE_KEY);
    if (!data) {
      throw new Error('لم يتم العثور على السيرة الذاتية');
    }
    return JSON.parse(data);
  },

  async saveCV(data: CVData): Promise<void> {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
  },

  async updateCV(id: string, data: CVData): Promise<void> {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
  },

  async deleteCV(id: string): Promise<void> {
    localStorage.removeItem(STORAGE_KEY);
  },
};
