import React, { useState, useEffect } from 'react';
import {
  Paper,
  TextField,
  Button,
  IconButton,
  Grid,
  Typography,
  styled,
} from '@mui/material';
import { Add as AddIcon, Delete as DeleteIcon } from '@mui/icons-material';
import { CVData } from '../../types/CVTypes';
import { CVService } from '../../services/api/cvService';
import { CVSection } from './CVSection';
import { RequiredLabel } from '../shared/RequiredLabel';
import { LoadingOverlay } from '../shared/LoadingOverlay';

const useStyles = styled('div')(({ theme }) => ({
  root: {
    padding: theme.spacing(4),
    maxWidth: 800,
    margin: '0 auto',
  },
  field: {
    marginBottom: theme.spacing(2),
  },
  arrayField: {
    marginBottom: theme.spacing(1),
  },
  addButton: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(3),
  },
  deleteButton: {
    marginTop: theme.spacing(2),
  },
  submitButton: {
    marginTop: theme.spacing(4),
  },
}));

const emptyExperience = {
  title: '',
  company: '',
  period: '',
  description: [''],
};

const emptyEducation = {
  degree: '',
  institution: '',
  period: '',
  details: '',
};

export const CVEdit: React.FC = () => {
  const classes = useStyles();
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [cv, setCV] = useState<CVData>({
    personalInfo: {
      fullName: '',
      title: '',
      email: '',
      phone: '',
      location: '',
    },
    summary: '',
    experience: [{ ...emptyExperience }],
    education: [{ ...emptyEducation }],
    skills: [''],
  });

  useEffect(() => {
    const loadCV = async () => {
      try {
        const data = await CVService.getCVById('1');
        setCV(data);
      } catch (err) {
        // إذا لم يتم العثور على السيرة الذاتية، نستخدم القيم الافتراضية
        if (err instanceof Error && err.message !== 'لم يتم العثور على السيرة الذاتية') {
          setError('حدث خطأ أثناء تحميل السيرة الذاتية');
        }
      } finally {
        setLoading(false);
      }
    };

    loadCV();
  }, []);

  const handlePersonalInfoChange = (field: keyof typeof cv.personalInfo) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setCV({
      ...cv,
      personalInfo: {
        ...cv.personalInfo,
        [field]: event.target.value,
      },
    });
  };

  const handleSummaryChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCV({
      ...cv,
      summary: event.target.value,
    });
  };

  const handleExperienceChange = (index: number, field: keyof typeof emptyExperience) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    const newExperience = [...cv.experience];
    newExperience[index] = {
      ...newExperience[index],
      [field]: event.target.value,
    };
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const handleExperienceDescriptionChange = (expIndex: number, descIndex: number) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    const newExperience = [...cv.experience];
    const newDescription = [...newExperience[expIndex].description];
    newDescription[descIndex] = event.target.value;
    newExperience[expIndex] = {
      ...newExperience[expIndex],
      description: newDescription,
    };
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const handleEducationChange = (index: number, field: keyof typeof emptyEducation) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    const newEducation = [...cv.education];
    newEducation[index] = {
      ...newEducation[index],
      [field]: event.target.value,
    };
    setCV({
      ...cv,
      education: newEducation,
    });
  };

  const handleSkillChange = (index: number) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    const newSkills = [...cv.skills];
    newSkills[index] = event.target.value;
    setCV({
      ...cv,
      skills: newSkills,
    });
  };

  const addExperience = () => {
    setCV({
      ...cv,
      experience: [...cv.experience, { ...emptyExperience }],
    });
  };

  const removeExperience = (index: number) => {
    const newExperience = [...cv.experience];
    newExperience.splice(index, 1);
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const addEducation = () => {
    setCV({
      ...cv,
      education: [...cv.education, { ...emptyEducation }],
    });
  };

  const removeEducation = (index: number) => {
    const newEducation = [...cv.education];
    newEducation.splice(index, 1);
    setCV({
      ...cv,
      education: newEducation,
    });
  };

  const addSkill = () => {
    setCV({
      ...cv,
      skills: [...cv.skills, ''],
    });
  };

  const removeSkill = (index: number) => {
    const newSkills = [...cv.skills];
    newSkills.splice(index, 1);
    setCV({
      ...cv,
      skills: newSkills,
    });
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setSaving(true);
    try {
      await CVService.saveCV(cv);
    } catch (err) {
      if (err instanceof Error) {
        setError('حدث خطأ أثناء حفظ السيرة الذاتية');
      }
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return <LoadingOverlay open={true} />;
  }

  return (
    <Paper className={classes.root} elevation={3}>
      <form onSubmit={handleSubmit}>
        <CVSection title="المعلومات الشخصية">
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label={<RequiredLabel label="الاسم الكامل" required />}
                value={cv.personalInfo.fullName}
                onChange={handlePersonalInfoChange('fullName')}
                className={classes.field}
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label={<RequiredLabel label="المسمى الوظيفي" required />}
                value={cv.personalInfo.title}
                onChange={handlePersonalInfoChange('title')}
                className={classes.field}
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label={<RequiredLabel label="البريد الإلكتروني" required />}
                value={cv.personalInfo.email}
                onChange={handlePersonalInfoChange('email')}
                className={classes.field}
                required
                type="email"
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label={<RequiredLabel label="رقم الهاتف" required />}
                value={cv.personalInfo.phone}
                onChange={handlePersonalInfoChange('phone')}
                className={classes.field}
                required
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label={<RequiredLabel label="الموقع" required />}
                value={cv.personalInfo.location}
                onChange={handlePersonalInfoChange('location')}
                className={classes.field}
                required
              />
            </Grid>
          </Grid>
        </CVSection>

        <CVSection title="الملخص">
          <TextField
            fullWidth
            multiline
            rows={4}
            label={<RequiredLabel label="الملخص المهني" required />}
            value={cv.summary}
            onChange={handleSummaryChange}
            className={classes.field}
            required
          />
        </CVSection>

        <CVSection title="الخبرات">
          {cv.experience.map((exp, index) => (
            <div key={index} className={classes.arrayField}>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label={<RequiredLabel label="المسمى الوظيفي" required />}
                    value={exp.title}
                    onChange={handleExperienceChange(index, 'title')}
                    className={classes.field}
                    required
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label={<RequiredLabel label="الشركة" required />}
                    value={exp.company}
                    onChange={handleExperienceChange(index, 'company')}
                    className={classes.field}
                    required
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label={<RequiredLabel label="الفترة" required />}
                    value={exp.period}
                    onChange={handleExperienceChange(index, 'period')}
                    className={classes.field}
                    required
                    placeholder="مثال: 2019 - 2021"
                  />
                </Grid>
                {exp.description.map((desc, descIndex) => (
                  <Grid item xs={12} key={descIndex}>
                    <TextField
                      fullWidth
                      label={`الوصف ${descIndex + 1}`}
                      value={desc}
                      onChange={handleExperienceDescriptionChange(index, descIndex)}
                      className={classes.field}
                    />
                  </Grid>
                ))}
              </Grid>
              {cv.experience.length > 1 && (
                <Button
                  variant="outlined"
                  color="secondary"
                  onClick={() => removeExperience(index)}
                  className={classes.deleteButton}
                  startIcon={<DeleteIcon />}
                >
                  حذف الخبرة
                </Button>
              )}
            </div>
          ))}
          <Button
            variant="outlined"
            color="primary"
            onClick={addExperience}
            className={classes.addButton}
            startIcon={<AddIcon />}
          >
            إضافة خبرة
          </Button>
        </CVSection>

        <CVSection title="التعليم">
          {cv.education.map((edu, index) => (
            <div key={index} className={classes.arrayField}>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label={<RequiredLabel label="الدرجة العلمية" required />}
                    value={edu.degree}
                    onChange={handleEducationChange(index, 'degree')}
                    className={classes.field}
                    required
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label={<RequiredLabel label="المؤسسة التعليمية" required />}
                    value={edu.institution}
                    onChange={handleEducationChange(index, 'institution')}
                    className={classes.field}
                    required
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label={<RequiredLabel label="الفترة" required />}
                    value={edu.period}
                    onChange={handleEducationChange(index, 'period')}
                    className={classes.field}
                    required
                    placeholder="مثال: 2015 - 2019"
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="التفاصيل"
                    value={edu.details}
                    onChange={handleEducationChange(index, 'details')}
                    className={classes.field}
                    multiline
                    rows={2}
                  />
                </Grid>
              </Grid>
              {cv.education.length > 1 && (
                <Button
                  variant="outlined"
                  color="secondary"
                  onClick={() => removeEducation(index)}
                  className={classes.deleteButton}
                  startIcon={<DeleteIcon />}
                >
                  حذف التعليم
                </Button>
              )}
            </div>
          ))}
          <Button
            variant="outlined"
            color="primary"
            onClick={addEducation}
            className={classes.addButton}
            startIcon={<AddIcon />}
          >
            إضافة تعليم
          </Button>
        </CVSection>

        <CVSection title="المهارات">
          {cv.skills.map((skill, index) => (
            <div key={index} className={classes.arrayField}>
              <Grid container spacing={2} alignItems="center">
                <Grid item xs>
                  <TextField
                    fullWidth
                    label={`المهارة ${index + 1}`}
                    value={skill}
                    onChange={handleSkillChange(index)}
                    className={classes.field}
                  />
                </Grid>
                {cv.skills.length > 1 && (
                  <Grid item>
                    <IconButton
                      color="secondary"
                      onClick={() => removeSkill(index)}
                      size="small"
                    >
                      <DeleteIcon />
                    </IconButton>
                  </Grid>
                )}
              </Grid>
            </div>
          ))}
          <Button
            variant="outlined"
            color="primary"
            onClick={addSkill}
            className={classes.addButton}
            startIcon={<AddIcon />}
          >
            إضافة مهارة
          </Button>
        </CVSection>

        {error && (
          <Typography color="error" className={classes.field}>
            {error}
          </Typography>
        )}

        <Button
          type="submit"
          variant="contained"
          color="primary"
          fullWidth
          className={classes.submitButton}
          disabled={saving}
        >
          {saving ? 'جاري الحفظ...' : 'حفظ السيرة الذاتية'}
        </Button>
      </form>
      <LoadingOverlay open={saving} message="جاري حفظ السيرة الذاتية..." />
    </Paper>
  );
};
