import React, { useEffect, useState } from 'react';
import { 
  Paper, 
  Typography, 
  List, 
  ListItem, 
  ListItemText,
  makeStyles 
} from '@material-ui/core';
import { CVData } from '../../types/CVTypes';
import { CVService } from '../../services/api/cvService';
import { CVSection } from './CVSection';
import { LoadingOverlay } from '../shared/LoadingOverlay';

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(4),
    maxWidth: 800,
    margin: '0 auto',
  },
  header: {
    textAlign: 'center',
    marginBottom: theme.spacing(4),
  },
  title: {
    color: theme.palette.primary.main,
    marginTop: theme.spacing(1),
  },
  contactInfo: {
    marginTop: theme.spacing(2),
    color: theme.palette.text.secondary,
  },
  experienceItem: {
    marginBottom: theme.spacing(2),
  },
  period: {
    color: theme.palette.text.secondary,
    marginBottom: theme.spacing(1),
  },
  description: {
    marginTop: theme.spacing(1),
  },
  skillsList: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: theme.spacing(1),
  },
  skill: {
    backgroundColor: theme.palette.primary.light,
    color: theme.palette.primary.contrastText,
    padding: theme.spacing(0.5, 1.5),
    borderRadius: theme.shape.borderRadius,
  },
}));

export const CVPreview: React.FC = () => {
  const classes = useStyles();
  const [cv, setCV] = useState<CVData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadCV = async () => {
      try {
        const data = await CVService.getCVById('1'); // نستخدم ID ثابت للتجربة
        setCV(data);
      } catch (err) {
        setError('حدث خطأ أثناء تحميل السيرة الذاتية');
      } finally {
        setLoading(false);
      }
    };

    loadCV();
  }, []);

  if (loading) {
    return <LoadingOverlay open={true} />;
  }

  if (error || !cv) {
    return (
      <Typography color="error" align="center">
        {error || 'لم يتم العثور على السيرة الذاتية'}
      </Typography>
    );
  }

  return (
    <Paper className={classes.root} elevation={3}>
      <div className={classes.header}>
        <Typography variant="h4">{cv.personalInfo.fullName}</Typography>
        <Typography variant="h6" className={classes.title}>
          {cv.personalInfo.title}
        </Typography>
        <Typography variant="body1" className={classes.contactInfo}>
          {cv.personalInfo.email} | {cv.personalInfo.phone} | {cv.personalInfo.location}
        </Typography>
      </div>

      <CVSection title="الملخص">
        <Typography>{cv.summary}</Typography>
      </CVSection>

      <CVSection title="الخبرات">
        {cv.experience.map((exp, index) => (
          <div key={index} className={classes.experienceItem}>
            <Typography variant="h6">{exp.title}</Typography>
            <Typography variant="subtitle1">{exp.company}</Typography>
            <Typography variant="body2" className={classes.period}>
              {exp.period}
            </Typography>
            <List>
              {exp.description.map((desc, i) => (
                <ListItem key={i}>
                  <ListItemText primary={desc} />
                </ListItem>
              ))}
            </List>
          </div>
        ))}
      </CVSection>

      <CVSection title="التعليم">
        {cv.education.map((edu, index) => (
          <div key={index} className={classes.experienceItem}>
            <Typography variant="h6">{edu.degree}</Typography>
            <Typography variant="subtitle1">{edu.institution}</Typography>
            <Typography variant="body2" className={classes.period}>
              {edu.period}
            </Typography>
            <Typography>{edu.details}</Typography>
          </div>
        ))}
      </CVSection>

      <CVSection title="المهارات">
        <div className={classes.skillsList}>
          {cv.skills.map((skill, index) => (
            <div key={index} className={classes.skill}>
              {skill}
            </div>
          ))}
        </div>
      </CVSection>
    </Paper>
  );
};
