import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import {
  Paper,
  Typography,
  TextField,
  Button,
  IconButton,
  Box,
  styled
} from '@mui/material';
import { Add as AddIcon, Delete as DeleteIcon } from '@mui/icons-material';
import { CVData, Experience, Education } from '../../types/CVTypes';
import { CVService } from '../../services/api/cvService';

const StyledPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(4),
  maxWidth: 800,
  margin: '0 auto',
  '& .section': {
    marginBottom: theme.spacing(4),
  },
  '& .sectionTitle': {
    color: theme.palette.primary.main,
    marginBottom: theme.spacing(2),
  },
  '& .field': {
    marginBottom: theme.spacing(2),
  },
  '& .arrayField': {
    marginBottom: theme.spacing(2),
    padding: theme.spacing(2),
    border: `1px solid ${theme.palette.divider}`,
    borderRadius: theme.shape.borderRadius,
  },
  '& .addButton': {
    marginTop: theme.spacing(1),
  },
  '& .deleteButton': {
    float: 'right',
  },
}));

const initialCV: CVData = {
  personalInfo: {
    fullName: '',
    title: '',
    email: '',
    phone: '',
    location: '',
  },
  summary: '',
  experience: [],
  education: [],
  skills: [],
};

const emptyExperience: Experience = {
  title: '',
  company: '',
  period: '',
  description: [''],
};

const emptyEducation: Education = {
  degree: '',
  institution: '',
  period: '',
  details: '',
};

export const CVEdit: React.FC = () => {
  const [cv, setCV] = useState<CVData>(initialCV);
  const history = useHistory();

  const handlePersonalInfoChange = (field: keyof typeof cv.personalInfo) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setCV({
      ...cv,
      personalInfo: {
        ...cv.personalInfo,
        [field]: event.target.value,
      },
    });
  };

  const handleSummaryChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCV({
      ...cv,
      summary: event.target.value,
    });
  };

  const handleExperienceChange = (index: number, field: keyof Experience) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    const newExperience = [...cv.experience];
    newExperience[index] = {
      ...newExperience[index],
      [field]: event.target.value,
    };
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const handleExperienceDescriptionChange = (expIndex: number, descIndex: number) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    const newExperience = [...cv.experience];
    const newDescription = [...newExperience[expIndex].description];
    newDescription[descIndex] = event.target.value;
    newExperience[expIndex] = {
      ...newExperience[expIndex],
      description: newDescription,
    };
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const handleEducationChange = (index: number, field: keyof Education) => (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    const newEducation = [...cv.education];
    newEducation[index] = {
      ...newEducation[index],
      [field]: event.target.value,
    };
    setCV({
      ...cv,
      education: newEducation,
    });
  };

  const handleSkillsChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCV({
      ...cv,
      skills: event.target.value.split(',').map(skill => skill.trim()),
    });
  };

  const addExperience = () => {
    setCV({
      ...cv,
      experience: [...cv.experience, { ...emptyExperience }],
    });
  };

  const removeExperience = (index: number) => {
    const newExperience = [...cv.experience];
    newExperience.splice(index, 1);
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const addEducation = () => {
    setCV({
      ...cv,
      education: [...cv.education, { ...emptyEducation }],
    });
  };

  const removeEducation = (index: number) => {
    const newEducation = [...cv.education];
    newEducation.splice(index, 1);
    setCV({
      ...cv,
      education: newEducation,
    });
  };

  const addExperienceDescription = (expIndex: number) => {
    const newExperience = [...cv.experience];
    newExperience[expIndex].description.push('');
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const removeExperienceDescription = (expIndex: number, descIndex: number) => {
    const newExperience = [...cv.experience];
    newExperience[expIndex].description.splice(descIndex, 1);
    setCV({
      ...cv,
      experience: newExperience,
    });
  };

  const handleSubmit = async () => {
    try {
      await CVService.saveCV(cv);
      history.push('/');
    } catch (error) {
      console.error('Error saving CV:', error);
    }
  };

  return (
    <StyledPaper elevation={3}>
      <div className="section">
        <Typography variant="h5" className="sectionTitle">
          المعلومات الشخصية
        </Typography>
        <TextField
          fullWidth
          label="الاسم الكامل"
          value={cv.personalInfo.fullName}
          onChange={handlePersonalInfoChange('fullName')}
          className="field"
        />
        <TextField
          fullWidth
          label="المسمى الوظيفي"
          value={cv.personalInfo.title}
          onChange={handlePersonalInfoChange('title')}
          className="field"
        />
        <TextField
          fullWidth
          label="البريد الإلكتروني"
          value={cv.personalInfo.email}
          onChange={handlePersonalInfoChange('email')}
          className="field"
        />
        <TextField
          fullWidth
          label="رقم الهاتف"
          value={cv.personalInfo.phone}
          onChange={handlePersonalInfoChange('phone')}
          className="field"
        />
        <TextField
          fullWidth
          label="الموقع"
          value={cv.personalInfo.location}
          onChange={handlePersonalInfoChange('location')}
          className="field"
        />
      </div>

      <div className="section">
        <Typography variant="h5" className="sectionTitle">
          الملخص
        </Typography>
        <TextField
          fullWidth
          multiline
          rows={4}
          label="ملخص السيرة الذاتية"
          value={cv.summary}
          onChange={handleSummaryChange}
          className="field"
        />
      </div>

      <div className="section">
        <Typography variant="h5" className="sectionTitle">
          الخبرات
        </Typography>
        {cv.experience.map((exp, expIndex) => (
          <Box key={expIndex} className="arrayField">
            <IconButton
              className="deleteButton"
              onClick={() => removeExperience(expIndex)}
            >
              <DeleteIcon />
            </IconButton>
            <TextField
              fullWidth
              label="المسمى الوظيفي"
              value={exp.title}
              onChange={handleExperienceChange(expIndex, 'title')}
              className="field"
            />
            <TextField
              fullWidth
              label="الشركة"
              value={exp.company}
              onChange={handleExperienceChange(expIndex, 'company')}
              className="field"
            />
            <TextField
              fullWidth
              label="الفترة"
              value={exp.period}
              onChange={handleExperienceChange(expIndex, 'period')}
              className="field"
            />
            {exp.description.map((desc, descIndex) => (
              <Box key={descIndex} sx={{ display: 'flex', gap: 1 }}>
                <TextField
                  fullWidth
                  label="الوصف"
                  value={desc}
                  onChange={handleExperienceDescriptionChange(expIndex, descIndex)}
                  className="field"
                />
                <IconButton
                  onClick={() => removeExperienceDescription(expIndex, descIndex)}
                >
                  <DeleteIcon />
                </IconButton>
              </Box>
            ))}
            <Button
              startIcon={<AddIcon />}
              onClick={() => addExperienceDescription(expIndex)}
            >
              إضافة وصف
            </Button>
          </Box>
        ))}
        <Button
          variant="outlined"
          startIcon={<AddIcon />}
          onClick={addExperience}
          className="addButton"
        >
          إضافة خبرة
        </Button>
      </div>

      <div className="section">
        <Typography variant="h5" className="sectionTitle">
          التعليم
        </Typography>
        {cv.education.map((edu, eduIndex) => (
          <Box key={eduIndex} className="arrayField">
            <IconButton
              className="deleteButton"
              onClick={() => removeEducation(eduIndex)}
            >
              <DeleteIcon />
            </IconButton>
            <TextField
              fullWidth
              label="الدرجة العلمية"
              value={edu.degree}
              onChange={handleEducationChange(eduIndex, 'degree')}
              className="field"
            />
            <TextField
              fullWidth
              label="المؤسسة التعليمية"
              value={edu.institution}
              onChange={handleEducationChange(eduIndex, 'institution')}
              className="field"
            />
            <TextField
              fullWidth
              label="الفترة"
              value={edu.period}
              onChange={handleEducationChange(eduIndex, 'period')}
              className="field"
            />
            <TextField
              fullWidth
              label="التفاصيل"
              value={edu.details}
              onChange={handleEducationChange(eduIndex, 'details')}
              className="field"
            />
          </Box>
        ))}
        <Button
          variant="outlined"
          startIcon={<AddIcon />}
          onClick={addEducation}
          className="addButton"
        >
          إضافة تعليم
        </Button>
      </div>

      <div className="section">
        <Typography variant="h5" className="sectionTitle">
          المهارات
        </Typography>
        <TextField
          fullWidth
          multiline
          rows={2}
          label="المهارات (مفصولة بفواصل)"
          value={cv.skills.join(', ')}
          onChange={handleSkillsChange}
          className="field"
          helperText="أدخل المهارات مفصولة بفواصل، مثال: React, TypeScript, Node.js"
        />
      </div>

      <Button
        variant="contained"
        color="primary"
        size="large"
        fullWidth
        onClick={handleSubmit}
      >
        حفظ السيرة الذاتية
      </Button>
    </StyledPaper>
  );
};
